INSERT INTO messages VALUES (28,19,3,19,8,'randomsyllable','Coming back home..','*A dusty figure appears in the doorway*\r\n\r\n\"It\'s good to be back.\"\r\n\r\n\"I\'ll have a glass of water please.\"\r\n\r\n*He sits at a table in the corner near the door and proceeds to pull out a book and begins reading*\r\n\r\n\"Wonder when the others will arrive.\"','2001-05-07 17:43:52');
